import React from 'react';
import moment from 'moment';
import './Calendar.css';


moment().format();


class Calendar extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			width: props.width || "350px",
			style: props.style || {},
			dateContext: moment(),
			today: moment(),
			showMonthPopup: false,
			showYearPopup: false

		}
		this.state.style.width = this.state.width;
	}


	weekdays = moment.weekdays(); //List of weekdays
	weekdaysShort = moment.weekdaysShort(); //List of shortened days
	months = moment.months(); // List of each month

	year = () => {
		return this.state.dateContext.format('Y');
	}
	month = () => {
		return this.state.dateContext.format('MMMM');
	}
	daysInMonth = () => {
		return this.state.dateContext.daysInMonth();
	}
	currentDate = () => {
		return this.state.dateContext.get('date');
	}
	currentDay = () => {
		return this.state.dateContext.format('D');
	}

	firstDayofMonth = () => {
		let dateContext = this.state.dateContext;
		let firstDay = moment(dateContext).startOf('month').format('d'); //Day of week 0-6
		return firstDay;
	}

	SelectList = (props) => {
		let popup = props.data.map((data) => {
			return (
				<div key={data}>
					<a href="#">
						{data}
					</a>
				</div>
			);
		});

		return (
			<div className="month-popup">
				{popup}
			</div>
		);


	}

	MonthNav = () => {
		return (
			<span className="label-month">
				{this.month()}
				{this.state.showMonthPopup} ? <this.SelectList data={this.months} />
				}
			</span>


		)
	}

	render(){

		//Map the weekdays as <td>
		let weekdays = this.weekdaysShort.map((day) => {
			return (
				<td key={day} className="week-day">{day}</td>
			)
		});
		let blanks = [];
		for (let i = 0; i < this.firstDayofMonth(); i++){
			blanks.push(<td key={i*80} className="emptySlot">{" "}</td>);	
		}

		console.log("blanks: ", blanks);

		let daysInMonth = [];
		for (let d = 1; d <= this.daysInMonth(); d++){
			let className = (d === this.currentDay() ? "day current-day" : "day");
			daysInMonth.push(
				<td key={d} className={className}>
					<span>{d}</span>
				</td>
			);
		}

		console.log("days: ", daysInMonth);

		var totalSlots = [...blanks, ...daysInMonth];
		let rows = [];
		let cells = [];

		totalSlots.forEach((row, i) => {
			if ((i % 7) !== 0 ) {
				cells.push(row);
			}
			else{
				let insertRow = cells.slice();
				rows.push(insertRow);
				cells = [];
				cells.push(row);
			}
			if (i === totalSlots.length -1 ){
				let insertRow = cells.slice();
				rows.push(insertRow);
			}
		});

		let trElements = rows.map((d, i) => {
			return (
				<tr key={i*100}>
					{d}
				</tr>
			);
		})



		return(
			<div className="calendar-container" style={this.state.style}>
				<table className="calendar">
					<thead>
						<tr className="calendar-header">
							<td colSpan="5">
								<this.MonthNav/>
							</td>
						</tr>
					</thead>
					<tbody>
						<tr>
							{weekdays}
						</tr>
						{trElements}
					</tbody>

				</table>
			</div>
		);



	}


}

export default Calendar;
