import React from 'react';
import Button from 'react-bootstrap/Button';
import Scroll from './../Scroll/Scroll'
import Form from 'react-bootstrap/Form'
import './Settings.css';

class People extends React.Component{
	constructor(){
		super();
		this.state = {
			peopleList: [


			],
			fName: '',
			lName: '',
			email: '',
			tPassword: '',
			message: ''
		}
	}

	componentDidMount = () => {
		this.loadAllUsers();
	}

	loadAllUsers = () => {
		fetch('http://localhost:3000/people')
			.then(response => response.json())
			.then(users => this.setState({peopleList: users}));
	}

	addCall = () => {
		fetch('http://localhost:3000/people', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				firstName: this.state.fName,
				lastName: this.state.lName,
				email: this.state.email,
				password: this.state.tPassword,
				msg: this.state.message,
				department: this.props.department
			})
		})
			.then(response => response.json())
			.then(call => {
				if (call){
					this.loadCallTypes();
				}
			})
		this.setState({
			fName: '',
			lName: '',
			email: '',
			tPassword: '',
			message: ''
		})
	}

	onFNameChange = (e) => {
		this.setState({fName: e.target.value})
	}

	onLNameChange = (e) => {
		this.setState({lName: e.target.value})
	}

	onEmailChange = (e) => {
		this.setState({email: e.target.value})
	}

	onPasswordChange = (e) => {
		this.setState({tPassword: e.target.value})
	}

	onMsgChange = (e) => {
		this.setState({message: e.target.value})
	}

	render(){
		const {peopleList, fName, lName, email, tPassword, message} = this.state;

		let docList = [];
		for (let j = 0; j < peopleList.length; j++){
			docList.push(
				<li key={peopleList[j].id} id={peopleList[j].id}>
					{peopleList[j].lastName}, {peopleList[j].firstName}
					<input key={j} className='inp' onInput={() => console.log("click")} type="checkbox" />
					<Button key={-j-1} onClick={() => console.log("click")} className="delete butn" size="sm" variant="danger">Delete</Button>
				</li>
			)
		}

			return(
			<div className="body">
				<div className="left">

					<div className="top">
						<h4 className="subtitle">Staff List</h4>
					</div>
					<Scroll>
						<ul className="">
							{docList}
						</ul>
					</Scroll>

				</div>
				<div className="p right">
					<div className="top">
						<h4 className="subtitle">Add User</h4>
					</div>
					<Form id="callForm">
						<div id="box" style={{border:'2px solid black', height: '400px'}}>
							<Form.Group id="name">
								<Form.Control required type="text" onChange={this.onFNameChange} placeholder="First Name" />
							</Form.Group>
							<Form.Group id="name">
								<Form.Control required type="text" onChange={this.onLNameChange} placeholder="Last Name" />
							</Form.Group>
							<Form.Group id="email" controlId="formBasicEmail">
    							<Form.Control required type="email" onChange={this.onEmailChange} placeholder="Email" />
  							</Form.Group>
  							<Form.Group id="pword" controlId="formBasicPassword">
    							<Form.Control required type="password" onChange={this.onPasswordChange} placeholder="Temporary Password" />
						  </Form.Group>
						  <Form.Group controlId="exampleForm.ControlTextarea1">
    						<Form.Control id="message" as="textarea" onChange={this.onMsgChange} rows="6" placeholder="Message"/>
  							</Form.Group>
						</div>
						<div className="bottom">
							<Button id='callSub' variant="primary" type="submit">
								Submit
							</Button>
						</div>
					</Form>


				</div>
			</div>

		);



	}


}

export default People;
