import React from 'react';
import Calendar from './Calendar/Calendar';
import './Schedules.css';

const style = {
	position: "relative",
	margin: "50px auto"

}


class PerSchedule extends React.Component{
	constructor(props){
		super(props);
		this.state = {


		}
	}

	onDayClick = (e,day) => {
		alert(day);
	}

	adminSelect(isAdmin){
		if (isAdmin){
			return (
				<select className="doc selector">
  					<option selected>Ismail</option>
  					<option value="1">Menikefs</option>
  					<option value="2">Miskew</option>
 				 	<option value="3">Weiss</option>
				</select>

			);
		}
	}


	render(){
		const {testIsAdmin} = this.props;
		return(
			<div className="screen">
				<div className="top">
					{this.adminSelect(testIsAdmin)}
				</div>
				<div className="sked">
					<Calendar style={style} width="90%" onDayClick={(e,day) => this.onDayClick(e,day)}/>
				</div>
				<div className="bottom">

				</div>

			</div>
		);



	}


}

export default PerSchedule;
