import React from 'react';
import Button from 'react-bootstrap/button';
import Modal from 'react-bootstrap/Modal';
import './Account.css';


class Account extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			email: '',
			firstname: '',
			lastname: '',
			cPassword: '',
			nPassword: '',
			cNPassword: '',
			show: false,
			iPShow: false,
			cShow: false,
			nShow: false 
		}
	}

	componentDidMount = () => {
		this.loadAccount();
	}

	loadAccount = () => {
		fetch('http://localhost:3000/account/'+this.props.user.id)
			.then(response => response.json())
			.then(user => this.setState({
				email: user.email,
				firstname: user.firstname,
				lastname: user.lastname
			}));
	}

	onEmailChange = (event) => {
		this.setState({email: event.target.value})
	}

	onFNameChange = (event) => {
		this.setState({firstname: event.target.value})
	}

	onLNameChange = (event) => {
		this.setState({lastname: event.target.value})
	}

	onCPasswordChange = (event) => {
		this.setState({cPassword: event.target.value})
	}

	onNPasswordChange = (event) => {
		this.setState({nPassword: event.target.value})
	}

	onCNPasswordChange = (event) => {
		this.setState({cNPassword: event.target.value})
	}

	toggleShow = () => {
		this.setState({show: !this.state.show})
	}

	toggleIPShow = () => {
		this.setState({iPShow: !this.state.iPShow})
	}

	toggleCShow = () => {
		this.setState({cShow: !this.state.cShow})
	}

	onSubmitBasic = () => {
		if (
			this.state.firstname.length > 0 && 
			this.state.lastname.length > 0 &&
			this.props.validateEmail(this.state.email)
		){

			fetch('http://localhost:3000/account/'+this.props.user.id, {
				method: 'put',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					email: this.state.email,
					firstname: this.state.firstname,
					lastname: this.state.lastname
				})
			})
				.then(response => response.json())
				.then(user => {
					if (user.email){
						this.setState({
							email: user.email,
							firstname: user.firstname,
							lastname: user.lastname
						})
						this.props.loadUser(user);
						this.toggleShow();
					}
				})
		}
	}

	onSubmitAll = () => {
		if(	
			this.state.cPassword.length > 0 && 
			this.state.nPassword.length > 0 &&
			this.state.nPassword === this.state.cNPassword
		){			
			fetch('http://localhost:3000/account/'+this.props.user.id, {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					oldPassword: this.state.cPassword,
					newPassword: this.state.nPassword
				})
			})
				.then(response => response.json())
				.then(user => {
					if (user === 'incorrect password'){
						this.toggleIPShow();
					}
					else {
						console.log(user);
						this.onSubmitBasic();
						this.setState({
							cPassword: '',
							nPassword: '',
							cNPassword: ''
						})
					}
				})
		}
		else if (this.state.nPassword !== this.state.cNPassword){
			this.toggleCShow();
		}
		else if (this.state.nPassword.length === 0){
			this.toggleNShow();
		}
	}

	onSubmitChoose = () => {
		if (this.state.cPassword.length > 0){
			this.onSubmitAll();
		}
		else{
			this.onSubmitBasic();
		}
	}

	render(){
		const {show, iPShow, cShow} = this.state;
		return(
			<div>
				<div>
					<div className='accountInfo'>
						<h5 id='text'>Email Address</h5>
						<input value={this.state.email} onChange={this.onEmailChange}  type='email' name='email' className='accountInp'/>
						<h5 id='text'>First Name</h5>
						<input value={this.state.firstname} onChange={this.onFNameChange} type='text' name='first' className='accountInp'/>
						<h5 id='text'>Last Name</h5>
						<input value={this.state.lastname} onChange={this.onLNameChange} type='text' name='last' className='accountInp'/>
					</div>
					<div className='changePass'>
						<h2 id='header'>Change Password</h2>
						<h5 id='text'>Current Password</h5>
						<input value={this.state.cPassword} onChange={this.onCPasswordChange} type='password' name='cp' className='accountInp'/>
						<h5 id='text'>New Password</h5>
						<input value={this.state.nPassword} onChange={this.onNPasswordChange} type='password' name='np' className='accountInp'/>
						<h5 id='text'>Confirm New Password</h5>
						<input value={this.state.cNPassword} onChange={this.onCNPasswordChange} type='password' name='cnp' className='accountInp'/>
					</div>
					<Button onClick={this.onSubmitChoose} id="submit" variant="primary">Submit</Button>
				</div>
				<div className='modal'>
					<Modal show={show} onHide={this.toggleShow}>
						<Modal.Header closeButton>
							<Modal.Title id='modalTitle'>Success!</Modal.Title>
						</Modal.Header>
						<Modal.Body>
							<p>Your account information has been successfully changed!</p>
						</Modal.Body>
						<Modal.Footer>
							<Button onClick={this.toggleShow} variant="secondary" >
								Close
							</Button>
						</Modal.Footer>
					</Modal>
				</div>
				<div className='modal'>
					<Modal show={iPShow} onHide={this.toggleIPShow}>
						<Modal.Header closeButton>
							<Modal.Title id='modalTitle'>Incorrect Password</Modal.Title>
						</Modal.Header>
						<Modal.Body>
							<p>The current password you entered is incorrect.</p>
						</Modal.Body>
						<Modal.Footer>
							<Button onClick={this.toggleIPShow} variant="secondary" >
								Close
							</Button>
						</Modal.Footer>
					</Modal>
				</div>
				<div className='modal'>
					<Modal show={cShow} onHide={this.toggleCShow}>
						<Modal.Header closeButton>
							<Modal.Title id='modalTitle'>Error</Modal.Title>
						</Modal.Header>
						<Modal.Body>
							<p>The new passwords you entered do not match</p>
						</Modal.Body>
						<Modal.Footer>
							<Button onClick={this.toggleCShow} variant="secondary" >
								Close
							</Button>
						</Modal.Footer>
					</Modal>
				</div>
				<div className='modal'>
					<Modal show={nShow} onHide={this.toggleNShow}>
						<Modal.Header closeButton>
							<Modal.Title id='modalTitle'>Error</Modal.Title>
						</Modal.Header>
						<Modal.Body>
							<p>Please enter a valid password.</p>
						</Modal.Body>
						<Modal.Footer>
							<Button onClick={this.toggleNShow} variant="secondary" >
								Close
							</Button>
						</Modal.Footer>
					</Modal>
				</div>
			</div>
		);
	}
}

export default Account;
