import React from 'react';
import Button from 'react-bootstrap/Button';
import Scroll from './../Scroll/Scroll'
import Form from 'react-bootstrap/Form'
import './Settings.css';

class Entries extends React.Component{
	constructor(){
		super();
		this.state = {
			callList: [],
			callName: '',
			isactive: false,
			add: true,
			id: -1
		}
	}

	componentDidMount = () => {
	}

	addOrEdit = () => {
		if (this.state.add){
			this.addCall();
		}
		else{
			this.editCall();
		}
	}

	addCall = () => {
		if (this.state.callName.length > 0){
			fetch('http://localhost:3000/callTypes', {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					name: this.state.callName,
					active: this.state.isactive
				})
			})
				.then(response => response.json())
				.then(call => {
					if (call){
						this.loadCallTypes();
					}
				})
			this.setState({
				callName: '',
				isactive: false
			})
		}
	}

	deleteCall = (e) => {
		fetch('http://localhost:3000/callTypes', {
			method: 'delete',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				id: parseInt(e.target.parentNode.id,10)
			})
		})
			.then(response => response.json())
			.then(calls => {
				if (calls){
					this.loadCallTypes();
				}
			})
	}

	editCall = () => {
		if (this.state.callName.length > 0){
			fetch('http://localhost:3000/callTypes', {
				method: 'put',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					name: this.state.callName,
					active: this.state.isactive,
					id: parseInt(this.state.id,10)
				})
			})
				.then(response => response.json())
				.then(call => {
					if (call){
						this.loadCallTypes();
					}
				})
			this.setState({
				callName: '',
				isactive: false,
				add: true,
				id: -1
			})
		}
	}

	onNameChange = (e) => {
		this.setState({callName: e.target.value});
	}

	onActiveChange = () => {
		this.setState({isactive: !this.state.isactive});
	}

	onEdit = (e) => {
		const id = parseInt(e.target.parentNode.id,10)
		for (let i = 0; i < this.state.callList.length; i++){
			if (this.state.callList[i].id === id){
				this.setState({
					callName: this.state.callList[i].name,
					isactive: this.state.callList[i].active,
					add: false,
					id: id
				})
				break;
			}
		}
	}

	onCancel = () => {
		this.setState({
			callName: '',
			priority: 1,
			isactive: false,
			add: true,
			id: -1
		})
	}

	render(){
		const {callList, callName, isactive} = this.state;

		let priorityList = [];
		for (let j = 0; j < callList.length; j++){
			priorityList.push(
				<li key={callList[j].name} id={callList[j].id}>
					{callList[j].name}
					<Button key={j} onClick={this.onEdit} className="edit butn" size="sm" variant="secondary">Edit</Button>
					<Button key={-j-1} onClick={this.deleteCall} className="delete butn" size="sm" variant="danger">Delete</Button>
				</li>
			)
		}

		let prioritySelect = [];
		let max = 1;
		if (!this.state.add){
			max = 0;
		}
		for (let n = 1; n <= callList.length+max; n++){
			prioritySelect.push(<option value={n} key={n}>{n}</option>);
		}

		return(
			<div className="body">
				<div className="left">

					<div className="top">
						<h4 className="subtitle">Entry List</h4>
					</div>
					<Scroll>
						<ol className="">
							{priorityList}
						</ol>
					</Scroll>

				</div>
				<div className="ct right">
					<div className="top">
						<h4 className="subtitle">Add/Edit Calls</h4>
					</div>
					<Form id="callForm" >
						<div id="box" style={{border:'2px solid black', height: '200px'}}>
							<Form.Group id="name">
								<Form.Control required value={callName} onChange={this.onNameChange} type="text" placeholder="Name" />
							</Form.Group>
						  <Form.Group  id="activeCheck" controlId="formBasicCheckbox">
						    	<Form.Check onChange={this.onActiveChange} checked={isactive} type="checkbox" label="Active"/>
						 	 </Form.Group>
						</div>
						<div className="bottom">
							<Button onClick={this.onCancel} id='callSub' variant="secondary">
								Cancel
							</Button>
							<Button id='callSub' variant="primary" onClick={this.addOrEdit}>
								Submit
							</Button>
						</div>
					</Form>



				</div>
			</div>



		);



	}


}

export default Entries;
