import React from 'react';
import moment from 'moment';

import './Calendar.css';


moment().format();


class Calendar extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			width: props.width || "350px",
			style: props.style || {},
			dateContext: moment(),
			today: moment(),
			showMonthPopup: false,
			showYearPopup: false

		}
	}

	weekdays = moment.weekdays(); //List of weekdays
	weekdaysShort = moment.weekdaysShort(); //List of shortened days
	months = moment.months(); // List of each month

	year = () => {
		return this.state.dateContext.format('Y');
	}
	month = () => {
		return this.state.dateContext.format('MMMM');
	}
	daysInMonth = () => {
		return this.state.dateContext.daysInMonth();
	}
	currentDate = () => {
		return this.state.dateContext.get('date');
	}
	currentDay = () => {
		return this.state.dateContext.format('D');
	}

	firstDayofMonth = () => {
		let dateContext = this.state.dateContext;
		let firstDay = moment(dateContext).startOf('month').format('d'); //Day of week 0-6
		return firstDay;
	}


	render(){

		//Map the weekdays as <td>
		let weekdays = this.weekdaysShort.map((day) => {
			return (
				<td key={day} className="week-day">{day}</td>
			)
		});
		let blanks = [];
		for (let i = 0; i < firstDayofMonth(); i++){
			blanks.push(<td key={i} className="emptySlot">{" "}</td>);	
		}

		console.log("blanks: ", blanks);

		let trElements = [];

		return(
			<div className="calendar-container">
				<table className="calendar">
					<thead>
						<tr className="calendar-header">
						</tr>
					</thead>
					<tbody>
						<tr>
							{weekdays}
						</tr>
						{trElements}
					</tbody>

				</table>
			</div>
		);



	}


}

export default Calendar;
