import React, {Component} from 'react';
import logo from './logo.svg';
import Navbar from './Components/Navbar/Navbar';
import Login from './Components/Login/Login';
import Register from './Components/Register/Register';
import PerSchedule from './Components/Schedules/PerSchedule';
import PubSchedule from './Components/Schedules/PubSchedule';
import CSchedule from './Components/Schedules/PubSchedule';
import './App.css';





class App extends Component {
  constructor(){
    super();
    this.state = {
      isSignedIn: false,
      route: 'login',
      title: '',
      user:{
        id: '',
        firstname: '',
        lastname: '',
        email: '',
        colour: '',
        isadmin: false
      }
    }
  }

  /*
  Different cases:
  login
  register
  perSchedule
  pubSchedule
  cSchedule
  account
  messages
  holidays
  callTypes
  people
  */
  //Used for rendering when signed out
  outRenderSwitch(route){
    route === 'login'
    ? <Login/>
    : <Register/>
  }

  //used for rendering when signed in 
  inRenderSwitch(route){
    switch(route){
      case 'perSchedule': 
        this.setState({title:'Personal Schedule'})
        return <PerSchedule/>
      case 'pubSchedule': 
        this.setState({title:'Published Schedule'})
        return <PubSchedule/>
      case 'cSchedule': 
        this.setState({title:''})
        return <CSchedule/>
      case 'account': 
        this.setState({title:'Personal Schedule'})
        return <Account/>
      case 'messages': 
        this.setState({title:'Personal Schedule'})
        return <Messages/>
      case 'holidays': 
        this.setState({title:'Personal Schedule'})
        return <Holidays/>
      case 'callTypes': 
        this.setState({title:'Personal Schedule'})
        return <CallTypes/>
      case 'people': 
        this.setState({title:'Personal Schedule'})
        return <People/>
    }
  }


  render(){
     const {title, route} = this.state;
     return (
        <div className="App">
          { (! isSignedIn)
            ? this.outRenderSwitch(route)
            : <div>
                <Navbar/>
                <h1>{title}</h1>
                {this.inRenderSwitch(route)}
            </div>
          }

        </div>
      );
  }

}

export default App;
