import React, {Component} from 'react';
import logo from './logo.svg';
import Navbar from './Components/Navbar/Navbar';
import Login from './Components/Login/Login';
import Register from './Components/Register/Register';
import PerSchedule from './Components/Schedules/PerSchedule';
import PubSchedule from './Components/Schedules/PubSchedule';
import CSchedule from './Components/Schedules/CSchedule';
import Account from './Components/Account/Account';
import AMessages from './Components/Messages/AMessages';
import EMessages from './Components/Messages/EMessages';
import Holidays from './Components/Settings/Holidays/Holidays';
import CallTypes from './Components/Settings/CallTypes/CallTypes';
import People from './Components/Settings/People/People';
import './App.css';





class App extends Component {
  constructor(){
    super();
    this.state = {
      isSignedIn: false,
      route: 'login',
      title: '',
      user:{
        id: '',
        firstname: '',
        lastname: '',
        email: '',
        colour: '',
        isadmin: false
      }
    }
  }

  /*
  Different cases:
  login
  register
  perSchedule
  pubSchedule
  cSchedule
  account
  amessages
  emessages
  holidays
  callTypes
  people
  */
  //Used for rendering when signed out
  outRenderSwitch(route){
    (route === 'login') ? <Login/> : <Register/>
  }

  //used for rendering when signed in 
  inRenderSwitch(route){
    switch(route){
      case 'perSchedule': 
        this.setState({title:'Personal Schedule'})
        return <PerSchedule/>
      case 'pubSchedule': 
        this.setState({title:'Published Schedule'})
        return <PubSchedule/>
      case 'cSchedule': 
        this.setState({title:'Call Schedule'})
        return <CSchedule/>
      case 'account': 
        this.setState({title:'Account Information'})
        return <Account/>
      case 'amessages': 
        this.setState({title:'Messages'})
        return <AMessages/>
      case 'emessages': 
        this.setState({title:'Messages'})
        return <EMessages/>
      case 'holidays': 
        this.setState({title:'Holidays'})
        return <Holidays/>
      case 'callTypes': 
        this.setState({title:'Call Types'})
        return <CallTypes/>
      case 'people': 
        this.setState({title:'People'})
        return <People/>
    }
  }


  render(){
     const {title, route, isSignedIn} = this.state;
     return (
        <div className="App">
          { (! isSignedIn)
            ? this.outRenderSwitch(route)
            : <div>
                <Navbar/>
                <h1>{title}</h1>
                {this.inRenderSwitch(route)}
            </div>
          }

        </div>
      );
  }

}

export default App;
